package CoreJava;

public class convension {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=67;
		float j=7.376f;
		double s=76.7678;
		float k=(float)i+j;
		float m=i+j;
		System.out.println(k);
		System.out.println((double)k+s);
		System.out.println((int)m+s);

	}

}
