package CoreJava;

public class tables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10;i++)
		{
		System.out.println(i+"*2="+i*2);
		}
		int j=1;
		while(j<=10)
		{
		System.out.println(j+"*2="+j*2);
		j++;
		}
		int k=1;
		do
		{
		System.out.println(k+"*2="+k*3);
		k++;
		}while(k<=10);
	}

}
